import { createContext, useState } from "react";

export const ListContext = createContext();

export const ListProvider = ({children})  => {

    const [list, setList] = useState([{id: 1,title: 'comer' ,status: false }, {id: 2,title: 'dormir' ,status: false }]);

    const [newTask, setNewTask] = useState('llegue');
    const [updateData, setupdateData] = useState('');

    return(
        <ListContext.Provider value={{list, setList, newTask, setNewTask, updateData, setupdateData}}>

            {children}

        </ListContext.Provider>
    )
}
